"use strict";function initMap(){var t=new google.maps.Map(document.getElementById("map"),{zoom:13,center:{lat:43.653254,lng:-79.384132},mapTypeId:"terrain"});document.getElementById("results-button").addEventListener("click",function(){getNewCoords(),new google.maps.Polygon({paths:polygonCoords,strokeColor:"#FF0000",strokeOpacity:.8,strokeWeight:2,fillColor:"#FF0000",fillOpacity:.35}).setMap(t)})}function getNewCoords(){var t=document.getElementsByClassName("lat"),e=document.getElementsByClassName("lng");polygonCoords.length=0;for(var o=0;o<t.length;o++)polygonCoords.push({lat:parseFloat(t[o].value),lng:parseFloat(e[o].value)})}function startAgain(){document.getElementById("start-again").addEventListener("click",function(){location.reload()})}function init(){startAgain()}var polygonCoords=[];init();