"use strict";function initMap(){map=new google.maps.Map(document.getElementById("map"),{zoom:13,center:{lat:43.653254,lng:-79.384132},mapTypeId:"roadmap",styles:[]}),document.getElementById("results-button").addEventListener("click",function(){getNewCoords(),setPolygon=new google.maps.Polygon({paths:polygonCoords,strokeColor:"#FF0000",strokeOpacity:.8,strokeWeight:2,fillColor:"#FF0000",fillOpacity:.35}),addPolygon()})}function getNewCoords(){var o=document.getElementsByClassName("lat"),e=document.getElementsByClassName("lng");polygonCoords.length=0;for(var t=0;t<o.length;t++)polygonCoords.push({lat:parseFloat(o[t].value),lng:parseFloat(e[t].value)})}function addPolygon(){setPolygon.setMap(map),removePolygon()}function removePolygon(){document.getElementById("remove-boundary").addEventListener("click",function(){setPolygon.setMap(null)})}var polygonCoords=[],map=void 0,setPolygon=void 0;